pragma solidity ^0.6.2;

import "./ZDAO.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

////////////////////////////////////////////////////////////////////////////////////////////
/// @title ZDAOFactory
/// @author Christopher Dixon
////////////////////////////////////////////////////////////////////////////////////////////
/**
The ZDAOFactory contract is designed to produce and track individual ZDAO contracts
**/

contract ZDAOFactory is Ownable {
    /// @notice daoTracker is used to track a DAO's name to its address
    mapping(string => address) public daoTracker;
    /// @notice daoTokenTracker is used to track a DAO's rep token to the DAO's name
    mapping(string => address) public daoTokenTracker;
    /// @notice nameTaken is used to track whether or not a DAO exists with a certain name
    mapping(string => bool) public nameTaken;

    /**
     @notice createZDAO is a public function that allows a user to create their own zDAO
     @dev this function can create a rep token for the new DAO OR use an existing ERC20 token
     @param _daoName is the name of the DAO being created
     @param _tokenName is the name of the DAO's rep token(leave blank if using an existing token)
     @param _tokenSym is the symbol of the DAO's rep token(leave blank if using an existing token)
     @param _existingToken is the address of an existing ERC20 token that can represent the DAO's rep
                (this would be set to 0x0 if not applicapble)
     @param transferableRep is a bool representing whether or not the rep token is transferable
     @param _quorum is the minimum percentage of voteWeight needed for a vote to pass
     @param _proposalTime is the time in seconds that a proposal will be active for
    **/
    function createZDAO(
        string memory _daoName,
        string memory _tokenName,
        string memory _tokenSym,
        address _existingToken,
        bool transferableRep,
        uint256 _quorum,
        uint256 _proposalTime
    ) public {
        require(
            nameTaken[_daoName] != true,
            "Name Error: This DAO name is already taken"
        );
        if (_existingToken != 0x0000000000000000000000000000000000000000) {
            ZDAO dao =
                new ZDAO(
                    _daoName,
                    _tokenName,
                    _tokenSym,
                    0x0000000000000000000000000000000000000000,
                    msg.sender,
                    transferableRep,
                    _quorum,
                    _proposalTime
                );
            daoTracker[_daoName] = address(dao);
            daoTokenTracker[_daoName] = address(dao.repToken());
        } else {
            daoTokenTracker[_daoName] = _existingToken;
            ZDAO dao =
                new ZDAO(
                    _daoName,
                    _tokenName,
                    _tokenSym,
                    _existingToken,
                    msg.sender,
                    transferableRep,
                    _quorum,
                    _proposalTime
                );
            daoTracker[_daoName] = address(dao);
        }

        nameTaken[_daoName] = true;
    }

    /**
     @notice retrieveZDAO is used to retrieve a DAO's address by its name
     @dev this is a view only function for front end use
     @param _daoName is the name of the DAO who's address is being retrieved
     @return the address of the DAO
    **/
    function retrieveZDAO(string memory _daoName)
        public
        view
        returns (address)
    {
        return daoTracker[_daoName];
    }

    /**
     @notice retrieveZDAOToken is used to retrieve a DAO's rep token address by its name
     @dev this is a view only function for front end use
     @param _daoName is the name of the DAO who's token address is being retrieved
     @return the address of the DAO rep token
    **/
    function retrieveZDAOToken(string memory _daoName)
        public
        view
        returns (address)
    {
        return daoTokenTracker[_daoName];
    }

    /**
     @notice isDAOnameTaken is used to determine if a name for a DAO is currently in use
     @dev this is a view only function for front end use
     @param _daoName is the proposed name of a DAO
     @return a bool(true/false) for whether or not a DAO with the input name exists
    **/
    function isDAOnameTaken(string memory _daoName)
        public
        view
        returns (bool)
    {
        return nameTaken[_daoName];
    }
}

